public String ChkLoginId(){
	String[] usrGrpCode = userGroupCode.split(","); 
	
	for (int i=0;i<usrGrpCode.length;i++){
		int userGrpCode = Integer.parseInt(usrGrpCode[i].trim());
	
		int temp = docMgmtImpl.checkLoginId(loginId, userGrpCode);
			if(temp==1)		
		{
			htmlContent="true";
		}
		else{
			htmlContent="false";
		}
	}
	return "html";
	}

